commands/mute.js.content:
export default async function muteGroup(sock, msg) {
  const from = msg.key.remoteJid;
  const isGroup = from.endsWith('@g.us');

  if (!isGroup) {
    await sock.sendMessage(from, { text: '❌ This command only works in groups!' });
    return;
  }

  const text = msg.message.conversation || msg.message.extendedTextMessage?.text || '';
  const command = text.toLowerCase();

  try {
    if (command === '.mute') {
      // Mute group - only admins can send messages
      await sock.groupSettingUpdate(from, 'announcement');
      await sock.sendMessage(from, { text: '🔇 *Group Muted!*\n\nOnly admins can send messages now.' });
    } else if (command === '.unmute') {
      // Unmute group - everyone can send messages
      await sock.groupSettingUpdate(from, 'not_announcement');
      await sock.sendMessage(from, { text: '🔊 *Group Unmuted!*\n\nEveryone can send messages now.' });
    }
  } catch (err) {
    await sock.sendMessage(from, { text: `❌ Error: ${err.message}\n\nMake sure the bot is an admin!` });
  }
}

commands/ping.js.content:
export default {
  name: "ping",
  description: "Replies with Pong and latency",
  async execute(sock, msg) {
    const start = Date.now();
    await sock.sendMessage(msg.key.remoteJid, { text: "Pong 🏓" });
    const delta = Date.now() - start;
    // You can edit to calculate better latency; this gives a quick number.
    await sock.sendMessage(msg.key.remoteJid, { text: `Latency: ${delta} ms` });
  }
};

commands/tagall.js.content:
export default async function tagAll(sock, msg) {
  const from = msg.key.remoteJid;
  const isGroup = from.endsWith('@g.us');

  if (!isGroup) {
    await sock.sendMessage(from, { text: '❌ This command only works in groups!' });
    return;
  }

  try {
    const metadata = await sock.groupMetadata(from);
    const participants = metadata.participants;
    const mentions = participants.map(p => p.id);

    // Build message with all mentions
    let message = '📢 *Tagging All Group Members* 📢\n\n';
    participants.forEach((participant, index) => {
      message += `${index + 1}. @${participant.id.split('@')[0]}\n`;
    });

    await sock.sendMessage(from, { 
      text: message, 
      mentions: mentions 
    });
  } catch (err) {
    await sock.sendMessage(from, { text: `❌ Failed to tag all members: ${err.message}` });
  }
}

commands/warnings.js.content:
import fs from 'fs';
import path from 'path';

const dataDir = 'data';
const warningsFile = path.join(dataDir, 'warnings.json');

// Ensure data directory and warnings storage exist
if (!fs.existsSync(dataDir)) fs.mkdirSync(dataDir, { recursive: true });
if (!fs.existsSync(warningsFile)) fs.writeFileSync(warningsFile, JSON.stringify({}));

export async function warnUser(sock, msg) {
  const from = msg.key.remoteJid;
  const mentioned = msg.message.extendedTextMessage?.contextInfo?.mentionedJid;
  if (!mentioned || mentioned.length === 0) return await sock.sendMessage(from, { text: '❌ Mention a user to warn!' });

  const data = JSON.parse(fs.readFileSync(warningsFile, 'utf8'));

  for (let user of mentioned) {
    if (!data[from]) data[from] = {};
    if (!data[from][user]) data[from][user] = 0;
    data[from][user] += 1;

    await sock.sendMessage(from, { text: `⚠️ <@${user.split('@')[0]}> has been warned! Total warnings: ${data[from][user]}` });
  }

  fs.writeFileSync(warningsFile, JSON.stringify(data, null, 2));
}

export async function checkWarnings(sock, msg) {
  const from = msg.key.remoteJid;
  const mentioned = msg.message.extendedTextMessage?.contextInfo?.mentionedJid;
  const data = JSON.parse(fs.readFileSync(warningsFile, 'utf8'));

  if (!mentioned || mentioned.length === 0) {
    return await sock.sendMessage(from, { text: '❌ Mention a user to check warnings!' });
  }

  for (let user of mentioned) {
    const count = data[from]?.[user] || 0;
    await sock.sendMessage(from, { text: `⚠️ <@${user.split('@')[0]}> has ${count} warning(s)` });
  }
}

commands/welcome.js.content:
import fs from 'fs';
const welcomeFile = 'data/welcome.json';

if (!fs.existsSync(welcomeFile)) fs.writeFileSync(welcomeFile, JSON.stringify({}));

export async function welcomeMessage(sock, participant) {
  const from = participant.id.split('-')[0] + '@g.us';
  const data = JSON.parse(fs.readFileSync(welcomeFile, 'utf8'));
  const customMsg = data[from]?.welcome || '👋 Welcome to the group!';

  await sock.sendMessage(from, { text: `${customMsg}\n@${participant.id.split('@')[0]}`, mentions: [participant.id] });
}

export async function goodbyeMessage(sock, participant) {
  const from = participant.id.split('-')[0] + '@g.us';
  const data = JSON.parse(fs.readFileSync(welcomeFile, 'utf8'));
  const customMsg = data[from]?.goodbye || '👋 Goodbye!';

  await sock.sendMessage(from, { text: `${customMsg}\n@${participant.id.split('@')[0]}`, mentions: [participant.id] });
}
